#!/bin/bashscp -i /home/shijian/.ssh/id_rsa  /home/shijian/arthas.zip arthastest@10.200.19.47:~ssh -tt -i /home/shijian/.ssh/id_rsa arthastest@10.200.19.47 << eeooffif [ ! -d "arthas" ];then  unzip arthas.zipfiPID=\$(ps -ef|grep push-server.jar |grep -v grep | awk '{print \$2}')PORT=\$(sudo lsof -i tcp:8563|grep -v COMMAND | awk '{print \$2}')if [ "\$PID" != "\$PORT" ];then  sudo java -jar arthas/arthas-client.jar -c shutdown  sudo java -jar arthas/arthas-boot.jar  --tunnel-server 'ws://10.200.8.246:7777/ws'  --app-name push-server --attach-only \$PIDfiexiteeooffecho done!